#Nombre des journalistes dans ce array
def nombre_journalist
	puts "il y a #{@tab.length} journalistes dans ce array"	
end

#handle contiennent un numéro
def handle_avec_numero
	t = Array.new
	t = @tab.select {|v| v =~ /[0123456789]/ }
	puts "le handle contiennent de numero sont #{t.length} "
end

#handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ?
def handle_four_lettre
	t = @tab.grep(/[Aa]ude/) 
	puts t.inspect
end

#Combien commencent par une majuscule (première lettre juste après le @)

def commence_par_maj
	maj = Array.new
	maj = @tab.select {|y| y =~/@[A-Z]/ }
	puts "Les nombres de majuscule contenu dans le handle sont : #{maj.length}"
end

#Combien y a-t-il de underscore _ dans tous les pseudos confondus
def contains_underscore
	underscore = 0
	for n in @tab
		underscore = underscore + n.scan(/_/).length
	end
	puts "Les nombres des underscore sont : #{underscore}"
end

#Trie la liste de handle par ordre alphabétique
def tri
	ordre = @tab.sort_by {|alpha| alpha.downcase}
	puts ordre.inspect
	puts ordre.size
end

#Quelle est la position dans l'array de la personne @epenser
def position
	puts "@epenser se trouve au #{@tab.index('@epenser')}é position"
end

#Combien contiennent une majuscule
def contains_maj
	maj = 0
	for n in @tab
		if n.scan(/[A-Z]/).length == 1
			maj = maj + 1
		end
	end
	puts "il y a #{maj} majuscule dans le tableau" 
end

#Quels sont les 50 handles les plus courts de ce array
def fifty_handle_short
	val = @tab.sort_by(&:length)
	tab = val[0..49].inspect
	puts "les 50 handles les plus courts de ce array sont : \n #{tab}"
end

def menu
	puts "Entrer votre choix?"
	puts "1.Nombre des journalistes dans ce array" 
	puts "2.Nombre d'handle contiennent un numéro"
	puts "3.Nombre d'handle contiennent les 4 lettres du prénom 'Aude' à la suite"
	puts "4.Nombre d'handle commencent par une majuscule (première lettre juste après le @)"
	puts "5.Nombre d'handle contiennent une majuscule" 
	puts "6.Nombre underscore _ dans tous les pseudos confondus"
	puts "7.Trie la liste de handle par ordre alphabétique"
	puts "8.les 50 handles les plus courts de ce array"
	puts "9.la position dans l'array de la personne @epenserputs"
	print ">> "

	choix = gets.chomp.to_i

	if choix == 1
		nombre_journalist
	elsif choix == 2
		handle_avec_numero
	elsif choix == 3
		handle_four_lettre
	elsif choix == 4
		commence_par_maj
	elsif choix == 5
		contains_maj
	elsif choix == 6
		contains_underscore
	elseif choix == 7
		tri
	elsif choix == 8
		fifty_handle_short
	elsif choix == 9
		position
	else  puts "Erreur, veuillez choisir un de ces chiffres!"
	end
end

menu #permet a vos utilisateur de choisir ce qu'ils veulent savoir